import React, { useState, useEffect } from 'react';
const Asana = require('asana');
const date = new Date();

let client = Asana.ApiClient.instance;
let token = client.authentications['token'];
token.accessToken = '2/1207153187462584/1207160651338176:ae47e627318906c853675585bb006e95';

let tasksApiInstance = new Asana.TasksApi();
let opts = { 
    'limit': 5, 
    'project': "1207158378702041", 
    'completed_since': date,
    'opt_fields': "name,due_on"
};

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [offset, setOffset] = useState(0); // Initialize offset with 0

  const fetchTasks = (offset) => {
    tasksApiInstance.getTasks({ ...opts, offset }) // Include the offset in the request
      .then((result) => {
        setTasks(prevTasks => [...prevTasks, ...result.data]); // Append new tasks to the existing tasks array
        setOffset(offset + result.data.length); // Update the offset for the next request
      })
      .catch((error) => {
        console.error(error.response.body);
      });
  };

  useEffect(() => {
    fetchTasks(offset); // Fetch tasks when the component mounts
  }, []); // Empty dependency array ensures useEffect runs only once

  const handleShowMore = () => {
    fetchTasks(offset); // Fetch more tasks when the "Show More" button is clicked
  };

  return (
    <div>
      <h1>Task List</h1>
      <ul>
        {/* Map over the tasks array and render each task */}
        {tasks.map(task => (
          <li key={task.gid}>
            {/* Display the task title */}
            {task.name} - Due on: {task.due_on}
            {/* Optionally display a completion status */}
          </li>
        ))}
      </ul>
      <button onClick={handleShowMore}>Show More</button>
    </div>
  );
};

export default TaskList;
