import React, { useState, useEffect } from 'react';
const Asana = require('asana');
const date = new Date();

let client = Asana.ApiClient.instance;
let token = client.authentications['token'];
token.accessToken = '2/1207153187462584/1207160651338176:ae47e627318906c853675585bb006e95';

let tasksApiInstance = new Asana.TasksApi();
let opts = { 
    'limit': 5, 
    'project': "1207158378702041", 
    'completed_since': date,
    'opt_fields': "name,due_on"
};

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [nextOffset, setNextOffset] = useState(null); // Initialize nextOffset with null
  const [initialFetch, setInitialFetch] = useState(true); // Track if initial fetch is done

  const fetchTasks = (nextOffset) => {
    const options = initialFetch ? opts : { ...opts, nextOffset }; // Include offset conditionally
    tasksApiInstance.getTasks(options)
      .then((result) => {
        if (initialFetch) {
          setTasks(result.data); // Set tasks directly for the initial fetch
          setInitialFetch(false); // Update initial fetch status
        } else {
          setTasks(prevTasks => [...prevTasks, ...result.data]); // Append new tasks to the existing tasks array
        }
        if (result.next_page) {
          setNextOffset(result.next_page.offset); // Update nextOffset with the offset for the next request
        }
      })
      .catch((error) => {
        console.error(error.response ? error.response.body : error); // Log the error response body if available
      });
  };

  useEffect(() => {
    fetchTasks(); // Fetch tasks when the component mounts without offset
  }, []); // Empty dependency array ensures useEffect runs only once

  const handleShowMore = () => {
    fetchTasks(nextOffset); // Fetch more tasks when the "Show More" button is clicked with the next offset
  };

  return (
    <div>
      <h1>Task List</h1>
      <ul>
        {/* Map over the tasks array and render each task */}
        {tasks.map(task => (
          <li key={task.gid}>
            {/* Display the task title */}
            {task.name} - Due on: {task.due_on}
            {/* Optionally display a completion status */}
          </li>
        ))}
      </ul>
      {/*nextOffset &&*/ <button onClick={handleShowMore}>Show More</button>} {/* Render the button only if nextOffset is available */}
    </div>
  );
};

export default TaskList;
